Set(varCounter,0);
Set(varOverlay,false);
Set(varReset,true);
Set(varReset,false);
Set(varReset,true);
Collect(colDrinkOrder,
    {
        id: CountRows(colDrinkOrder) + 1,
        type: varDrinkType,
        size: " ",
        milk: " ",
        flavor: " ",
        temp: " ",
        cup: " ",
        shots: " ",
        notes: " "

    }
);
