SortByColumns(
        GroupBy(
            Filter(
                datVariants,
                VariantType <> "Milk",
                VariantType <> "Flavor",
                VariantType <> "Food", 
                VariantType <> "Extra Shot"
            ), 
            "VariantType",
            "GroupedData"
        ),
        "VariantType", 
        ["Size", "Temperature", "Cup", "Helper"]
    )
);

ClearCollect(colDataView1,
Table(
    {

    }
)
)

Switch(ThisItem.VariantType,
    "Size",
    Patch(colDrinkOrder,
    LookUp(colDrinkOrder, id = 1),
    {
        size: option_radio.Selected.Value
    }
    ),
    "Milk",
    Patch(colDrinkOrder,
    LookUp(colDrinkOrder, id = 1),
    {
        milk: option_radio.Selected.Value
    }
    ),
    "Flavor",
    Patch(colDrinkOrder,
    LookUp(colDrinkOrder, id = 1),
    {
        flavor: option_radio.Selected.Value
    }
    ),
    "Temperature",
    Patch(colDrinkOrder,
    LookUp(colDrinkOrder, id = 1),
    {
        temp: option_radio.Selected.Value
    }
    ),
    "Cup",
    Patch(colDrinkOrder,
    LookUp(colDrinkOrder, id = 1),
    {
        cup: option_radio.Selected.Value
    }
    ),
    " "
)
